import React, { useEffect, useState } from "react";
import {ButtonWithIcon} from "../../common/Button";
import Card from "../../common/Card";
import { FaShareAlt } from 'react-icons/fa';
import "./index.css";
import Dropdown from '../../common/Dropdown';
import CardFooter from "./Footer";
import CardHeader from "./Header";
import SharedMember from "./SharedMember";
import {users, sharedData} from "../../models/users";
import { _userToDropdown, _userToShareMember } from "../../transforms";
import { ShareProvider, useShareContext } from "./ShareContext";


const QuestionMark = () => {
    return <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
    <path d="M4.48511 5C4.85118 4.22321 5.83895 3.66667 7.00004 3.66667C8.4728 3.66667 9.6667 4.5621 9.6667 5.66667C9.6667 6.59963 8.81496 7.38338 7.66285 7.6044C7.30125 7.67377 7.00004 7.96514 7.00004 8.33333M7 10.3333H7.00667M13 7C13 10.3137 10.3137 13 7 13C3.68629 13 1 10.3137 1 7C1 3.68629 3.68629 1 7 1C10.3137 1 13 3.68629 13 7Z" stroke="#6B7280" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
    </svg>;
}

const ColorComponent = ({
    option,
    isSelected=false,
    onItemClick
}) => {
    return (<div 
                key={option.value} 
                onClick={() => onItemClick(option)} 
                className={`dropdown-item-person ${isSelected && "selected"}`}
            >
                <img  src={'https://images.unsplash.com/photo-1664574654529-b60630f33fdb?ixlib=rb-1.2.1&ixid=MnwxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=987&q=80'}  className="profile-image" />
                <span>{option.label}</span>
                {/* <div>check</div> */}
        </div>) 
}

const ShapesComponent = ({
    option,
    isSelected=false,
    onItemClick
}) => {
    return <div 
                key={option.value} 
                onClick={() => onItemClick(option)} 
                className={`dropdown-item-person ${isSelected && "selected"}`}
            >
                <div className="dropdown-item-legend">
                    {
                        <span>{option.label[0]}</span>
                    }
                </div>
                <span>
                    {
                        option.label
                    }
                </span>
        </div>  
}

const SearchFooterComponent = () => {
    return <div style={{display: 'flex', 

                alignItems: "center",
                gap: '10px'
            }}>
                <QuestionMark></QuestionMark>
                <span>learn about sharing</span>
            </div>
}


function Share({}) {

    useEffect(() => {
        // we will call APIs and set the data 
        // setting data can happen in service layer I am diretly setting from here for assignments sake
        setSharedUsers(sharedData);
        setAllUsers(users);
    }, [])

    const [selectedUsers, setSelectedUsers] = useState([]);

    const {sharedUsers, allUsers, setSharedUsers, setAllUsers} = useShareContext();

    const options = _userToDropdown(allUsers);
    options.map((option) => {
        if (option?.group === "person") {
            return option.renderer = ColorComponent;
        } else if (option?.group === "group") {
            return option.renderer = ShapesComponent;
        }
    })

    const handleInvite = () => {
        console.log("Invite");
        setSharedUsers([...sharedUsers, ...selectedUsers]);
    }

    const onUserSelect = (data = []) => {
        console.log("Selected Users", data);
        let temp = [];
        for (let i=0;i<data.length;i++) {
            for (let j=0;j<allUsers.length;j++) {
                if (data[i].id === allUsers[j].id && sharedUsers.indexOf(data[i].id) < 0) {
                    temp.push(allUsers[j]);
                }
            }
        }
        console.log("TEMp ", temp);
        setSelectedUsers(temp);
    }

    return <div>
        <ButtonWithIcon classNames="share-button" title={'Share'} IconComponent={<FaShareAlt />}/>
        <Card
            style={{
                width: '512px',
            }}
            header={{
                component: <CardHeader />
            }}
            footer= {{
                component: <CardFooter />,
            }}
        >
            <div>
                <Dropdown 
                    isMulti={true}
                    placeHolder={"People emails and groups"}
                    options={options}
                    isSearchable={true}
                    enableGrouping
                    footerComponent={<SearchFooterComponent />}
                    showCaretIcon={false}
                    rightCtaProps={{
                        onClick:handleInvite,
                        text: 'Invite',
                        visible: true,
                    }}
                    onChange={onUserSelect}
                />
            </div>
            <div style={{marginBottom: '40px'}} >
                {
                    _userToShareMember(sharedUsers).map((user) => {
                        return <SharedMember text={user.text} subText={user.subText} />
                    })
                }
            </div>
        </Card>
    </div>
}


const ExportWrapper = () => {
    return <ShareProvider>
        <Share />
    </ShareProvider>
}

export default ExportWrapper;
