import React, {useEffect, useState, useRef} from "react";

import "./dropdown.css";

const Icon = () => {
  return (
    <svg height="20" width="20" viewBox="0 0 20 20">
      <path d="M4.516 7.548c0.436-0.446 1.043-0.481 1.576 0l3.908 3.747 3.908-3.747c0.533-0.481 1.141-0.446 1.574 0 0.436 0.445 0.408 1.197 0 1.615-0.406 0.418-4.695 4.502-4.695 4.502-0.217 0.223-0.502 0.335-0.787 0.335s-0.57-0.112-0.789-0.335c0 0-4.287-4.084-4.695-4.502s-0.436-1.17 0-1.615z"></path>
    </svg>
  );
};

const CloseIcon = () => {
    return (
        <svg height="20" width="20" viewBox="0 0 20 20">
            <path d="M14.348 14.849c-0.469 0.469-1.229 0.469-1.697 0l-2.651-3.030-2.651 3.029c-0.469 0.469-1.229 0.469-1.697 0-0.469-0.469-0.469-1.229 0-1.697l2.758-3.15-2.759-3.152c-0.469-0.469-0.469-1.228 0-1.697s1.228-0.469 1.697 0l2.652 3.031 2.651-3.031c0.469-0.469 1.228-0.469 1.697 0s0.469 1.229 0 1.697l-2.758 3.152 2.758 3.15c0.469 0.469 0.469 1.229 0 1.698z"></path>
        </svg>
    );
};

const QuestionMark = () => {
  return <svg width="14" height="14" viewBox="0 0 14 14" fill="none" xmlns="http://www.w3.org/2000/svg">
  <path d="M4.48511 5C4.85118 4.22321 5.83895 3.66667 7.00004 3.66667C8.4728 3.66667 9.6667 4.5621 9.6667 5.66667C9.6667 6.59963 8.81496 7.38338 7.66285 7.6044C7.30125 7.67377 7.00004 7.96514 7.00004 8.33333M7 10.3333H7.00667M13 7C13 10.3137 10.3137 13 7 13C3.68629 13 1 10.3137 1 7C1 3.68629 3.68629 1 7 1C10.3137 1 13 3.68629 13 7Z" stroke="#6B7280" stroke-width="2" stroke-linecap="round" stroke-linejoin="round"/>
  </svg>;
}

const Dropdown = ({ 
    placeHolder, 
    options, 
    isMulti, 
    isSearchable, 
    groupingEnabled, 
    footerComponent,
    rightCtaProps = {
      visible: false,
      text: 'Click',
      onClick: () => {}
    } }) => {
    const [showMenu, setShowMenu] = useState(false);
    const [selectedValue, setSelectedValue] = useState(isMulti ? [] : null);

    const [searchValue, setSearchValue] = useState("");
    const searchRef = useRef();


    useEffect(() => {
      const handleEnterOnSearch = (event) => {
        if (event.keyCode == 13) {
          if (searchRef?.current?.value?.length > 0 && getOptions(searchRef?.current?.value).length > 0) {
            // onItemClick(getOptions(searchRef?.current?.value)[0]);
            // setSearchValue("");
          }
          return true;
       }
      }
      document.addEventListener('keypress', handleEnterOnSearch)
      return () => {
        document.removeEventListener('keypress', handleEnterOnSearch)
      };
    }, [])

    useEffect(() => {
        setSearchValue("");
        if (showMenu && searchRef.current) {
            searchRef.current.focus();
        }
    }, [showMenu]);

    const onSearch = (e) => {
        setSearchValue(e.target.value);
    };

    const grouping = (options) => {
      let group = {
        others: []
      };
      for (let i=0;i<options.length;i++) {
        if (options[i].group !== undefined) {
          group[options[i].group] = (group[options[i].group] ||  []).push(options[i]) ;
        } else {
          group['others'].push(options[i])
        }
      }
      console.log({group})
      return options;
    }

    const getOptions = (query=searchValue) => {
        if (!query) {
            return options;
        }
        return options.filter((option) => option.label.toLowerCase().indexOf(query.toLowerCase()) >= 0);
    };

    const getDisplay = () => {
        if (!selectedValue || selectedValue.length === 0) {
            return isSearchable ? (
              <div className="search-box">
                  <input className={`${showMenu ? 'search-box-input-active' : ''}`} onChange={onSearch} placeholder={placeHolder} value={searchValue} ref={searchRef} />
              </div>) : 
              <div>
               {placeHolder}
              </div>
        }
        if (isMulti) {
        return (
            <>
              <div className="dropdown-search">
                    {selectedValue.map((option) => (
                        <div key={option.value} className="dropdown-tag-item">
                            {option.label}
                            <span onClick={(e) => onTagRemove(e, option)} className="dropdown-tag-close"><CloseIcon /></span>
                        </div>
                    ))}
                {/* </div> */}
                {isSearchable && (
                  <div className="search-box">
                      <input  className={`${showMenu ? 'search-box-input-active' : ''}`} onChange={onSearch} placeholder={placeHolder} value={searchValue} ref={searchRef} />
                  </div>)
                }
              </div>
            </> 
        );
    }
    return <div>{selectedValue.label}</div>;
  };

  const onItemClick = (option) => {
    let newValue;
    if (isMulti) {
        if (selectedValue.findIndex((o) => o.value === option.value) >= 0) {
            newValue = removeOption(option);
        } else {
            newValue = [...selectedValue, option];
        }
    } else {
        newValue = option;
    }
    setSelectedValue(newValue);
  }

  const isSelected = (option) => {
    // console.log("SELECTED VALUE ", selectedValue);
    if (isMulti) {
        return selectedValue.filter((o) => o.value === option.value).length > 0;
    }
    if(!selectedValue) {
        return false;
    }
    return selectedValue.value === option.value;
  }

    const removeOption = (option) => {
        return selectedValue.filter((o) => o.value !== option.value);
    };

    const onTagRemove = (e, option) => {
        e.stopPropagation();
        setSelectedValue(removeOption(option));
    };

  // console.log({options})

  useEffect(() => {
    const handler = () => setShowMenu(false);
    window.addEventListener('click', handler);
    return () => {
        window.addEventListener('click', handler);
    }
  }, [])

  const handleInputClick = (e) => {
    e.stopPropagation();
    setShowMenu(!showMenu);
  }

  const renderOptionComponent = (option) => {
    console.log("====", option);
    if (option.renderer) {
      return <option.renderer option={option} isSelected={isSelected(option)} onItemClick={onItemClick} />
    }
    return <div 
              key={option.value} 
              onClick={() => onItemClick(option)} 
              className={`dropdown-item ${isSelected(option) && "selected"}`}
            >
            {
              option.label
            }
            </div>; 
  }

  return (
    <div className={`dropdown-container ${showMenu ? 'dropdown-active' : ''}`}>
      {
        showMenu ?
        <div className="dropdown-search-bar search-bar-active">
          <div onClick={handleInputClick} className="dropdown-input">
            <div className="dropdown-selected-value">{getDisplay()}</div>
          </div>
          {
            rightCtaProps?.visible && 
            <div className="dropdown-right-active">
              <div className="dropdown-cta" onClick={rightCtaProps?.onClick}>
                <span>{rightCtaProps?.text}</span>
              </div>
            </div>
          }
      </div> :
        <div className="dropdown-search-bar">
          <div onClick={handleInputClick} className={`dropdown-input`}>
            <div className="dropdown-selected-value">{getDisplay()}</div>
            <Icon />
          </div>
          {
            rightCtaProps?.visible && 
            <div className="dropdown-right">
              <div onClick={rightCtaProps?.onClick}>
                {
                  rightCtaProps?.text
                }
              </div>
            </div>
          }
        </div>
      }

      {
        showMenu && <div className="dropdown-menu">
        {
            getOptions().map((option) => {
                return renderOptionComponent(option);    
            })
        }
          <div className="dropdown-footer">
            {
              footerComponent
            }
          </div>
        </div>
      }

    </div>
  );
};

export default Dropdown;
