import React from "react";
import {ButtonWithIcon} from "../../common/Button";
import Card from "../../common/Card";
import { FaShareAlt } from 'react-icons/fa';
import "./index.css";
import Dropdown from '../../common/Dropdown';

const ColorComponent = ({
    option,
    isSelected=false,
    onItemClick
}) => {
    console.log("++++++++ ", option)
    return (<div 
                key={option.value} 
                onClick={() => onItemClick(option)} 
                className={`dropdown-item-person ${isSelected && "selected"}`}
            >
                <img  src={'https://images.unsplash.com/photo-1664574654529-b60630f33fdb?ixlib=rb-1.2.1&ixid=MnwxMjA3fDF8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=987&q=80'}  className="profile-image" />
                <span>{option.label}</span>
                {/* <div>check</div> */}
        </div>) 
}

const ShapesComponent = ({
    option,
    isSelected=false,
    onItemClick
}) => {
    return <div 
                key={option.value} 
                onClick={() => onItemClick(option)} 
                className={`dropdown-item ${isSelected && "selected"}`}
            >
            {
                option.label
            }
        </div>  
}

function Share({}) {
    const options = [
        {label: 'green', value: 'green', group: 'color'},
        {label: 'red', value: 'red', group: 'color'},
        {label: 'blue', value: 'blue', group: 'color'},
        {label: 'yellow', value: 'yellow', group: 'color'},
        {label: 'grey', value: 'grey', group: 'color'},
        {label: 'cyan', value: 'cyan', group: 'color'},
        {label: 'bc', value: 'bc', group: 'color'},
        {label: 'xyz', value: 'xyz', group: 'color'},
        {label: 'asd', value: 'asd', group: 'color'},
        {label: 'Square', value: 'square', group: 'shapes'},
        {label: 'Circle', value: 'circle', group: 'shapes'},
        {label: 'Hexagone', value: 'hexagone', group: 'shapes'},
        {label: 'Parabola', value: 'parabola', group: 'shapes'}
    ]

    options.map((option) => {
        return option.renderer = ColorComponent;
    });

    let groups = {
        'color': {
            renderer: <ColorComponent />
        },
        'names': {
            renderer: <ShapesComponent />
        }
    }


    return <div>
        <ButtonWithIcon classNames="share-button" title={'Share'} IconComponent={<FaShareAlt />}/>
        <div style={{marginTop: '50px', width: '488px'}}>
            <Dropdown 
                isMulti={true}
                placeHolder={"People emails and groups"}
                options={options}
                isSearchable
            />
        </div>
        {/* <Card>

        </Card> */}
    </div>
}

export default Share;
